// EDIT YOUR BOOK CONCEPT HERE
const book_concept = "A forbidden romance between a mortal alchemist who can manipulate metals and a Fae prince exiled from his court. When she accidentally binds his magic to hers, they must work together to break the curse before it kills them both.";
const genre = "Romance/Fantasy (Romantasy)";
const target_words = 18000;

const timestamp = new Date().toISOString().replace(/[:.]/g, '-').substring(0, 19);

return {
  json: {
    book_concept: book_concept,
    genre: genre,
    target_words: target_words,
    book_id: timestamp
  }
};

