parameters.jsCode:
// Create 10 chapter items with all needed data
const outline = $input.item.json.choices[0].message.content;
const conceptData = $('Set Book Concept').item.json;

// INITIALIZE static data storage for chapters
const staticData = this.getWorkflowStaticData('global');
staticData.chapters = [];
staticData.book_concept = conceptData.book_concept;
staticData.genre = conceptData.genre;
staticData.book_id = conceptData.book_id;
staticData.outline = outline;

console.log('üìù Initialized chapter storage - ready to collect 10 chapters');

const chapters = [];
for (let i = 1; i <= 10; i++) {
  chapters.push({
    json: {
      chapter_number: i,
      outline: outline,
      book_concept: conceptData.book_concept,
      genre: conceptData.genre,
      book_id: conceptData.book_id
    }
  });
}

return chapters;

