parameters.jsCode:
// ACCUMULATE chapters in static data
const chapterNumber = $json.chapter_number;
const chapterContent = $input.item.json.choices[0].message.content;

const staticData = this.getWorkflowStaticData('global');

// Store this chapter
staticData.chapters.push({
  chapter_number: chapterNumber,
  chapter_content: chapterContent
});

const completedCount = staticData.chapters.length;
console.log(`‚úÖ Chapter ${chapterNumber} completed (${chapterContent.split(' ').length} words) - ${completedCount}/10 done`);

return {
  json: {
    chapter_number: chapterNumber,
    completed_count: completedCount
  }
};

// COMPILE MANUSCRIPT - This fires when loop is done
// Retrieve all chapters from static data
const staticData = this.getWorkflowStaticData('global');
const allChapters = staticData.chapters || [];

console.log(`\nüéâ LOOP COMPLETE! Compiling manuscript from ${allChapters.length} chapters`);

if (allChapters.length !== 10) {
  throw new Error(`Expected 10 chapters in static data, got ${allChapters.length}`);
}

// Sort by chapter number
const sorted = allChapters.sort((a, b) => a.chapter_number - b.chapter_number);

// Get book data from static data
const bookData = {
  book_concept: staticData.book_concept,
  genre: staticData.genre,
  book_id: staticData.book_id
};

// Create title
const words = bookData.book_concept.split(' ').slice(0, 5).join(' ');

let manuscript = `${words}\nA ${bookData.genre} Novel\n\n`;
manuscript += `‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê\n\n`;
manuscript += `‚ö†Ô∏è AI GENERATED - REQUIRES EDITING\n\n`;
manuscript += `‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê\n\n`;

sorted.forEach(item => {
  manuscript += `\n\nCHAPTER ${item.chapter_number}\n\n`;
  manuscript += item.chapter_content;
  manuscript += `\n\n`;
});

const wordCount = manuscript.split(/\s+/).length;
const fileName = `${bookData.book_id}_fiction.txt`;

console.log(`üìö Manuscript compiled: ${wordCount} words across 10 chapters`);

// CLEAN UP static data for next run
staticData.chapters = [];

return {
  json: {
    book_id: bookData.book_id,
    file_path: `books/manuscripts/${fileName}`,
    word_count: wordCount,
    manuscript: manuscript
  }
};
