// Get manuscript content from GitHub node
const inputData = $input.first().json;

// GitHub node can return content in different formats
let manuscript;

if (inputData.data) {
  // If data field exists, use it directly (already decoded)
  manuscript = inputData.data;
  console.log('‚ÑπÔ∏è  Using direct data field from GitHub');
} else if (inputData.content) {
  // If content field exists, decode from base64
  manuscript = Buffer.from(inputData.content, 'base64').toString('utf-8');
  console.log('‚ÑπÔ∏è  Decoded base64 content from GitHub');
} else if ($input.first().binary && $input.first().binary.data) {
  // Check if it came as binary data
  const binaryBuffer = await this.helpers.getBinaryDataBuffer(0, 'data');
  manuscript = binaryBuffer.toString('utf-8');
  console.log('‚ÑπÔ∏è  Converted from binary data');
} else {
  // Debug: log what we actually received
  console.log('GitHub node output:', JSON.stringify(inputData, null, 2));
  throw new Error('Could not find manuscript content. Check GitHub node output format.');
}

if (!manuscript || manuscript.trim().length === 0) {
  throw new Error('Manuscript content is empty');
}

const wordCount = manuscript.split(/\s+/).filter(w => w.length > 0).length;
console.log(`‚ÑπÔ∏è  Manuscript loaded: ${wordCount} words`);

return {
  json: {
    data: manuscript,
    filename: inputData.name || 'unknown',
    path: inputData.path || 'unknown',
    word_count: wordCount
  }
};

// Configuration
const target_word_count = 47000;
const book_id = new Date().toISOString().replace(/[:.]/g, '-').substring(0, 19);

// Known issues from editorial assessment
const known_issues = [
  'CHAPTER undefined numbering bug',
  'Chapters too short (avg 1507 words, target 2000-2500)',
  'Rushed middle (Ch 6-10)',
  'Purple prose (tapestry of, eyes locked, weight of)',
  'Telling vs showing',
  'Weak villain depth (Seraphina)',
  'Underused supporting character (Gideon)',
  'Unclear curse stakes (consume them is vague)',
  'Tome plotline loses focus',
  'Convenient solutions',
  'Repetitive phrasing'
];

return {
  json: {
    book_id: book_id,
    target_word_count: target_word_count,
    known_issues: known_issues,
    iteration: 0
  }
};

// Find the latest manuscript file
const files = $input.all();

// Filter for .txt files only
const txtFiles = files
  .filter(item => item.json.name && item.json.name.endsWith('.txt'))
  .map(item => ({
    name: item.json.name,
    path: item.json.path,
    download_url: item.json.download_url,
    // Extract timestamp from filename (format: 2026-01-04T08-32-49_fiction.txt)
    timestamp: item.json.name.split('_')[0]
  }));

if (txtFiles.length === 0) {
  throw new Error('No manuscript files found in books/manuscripts/');
}

// Sort by timestamp (newest first)
txtFiles.sort((a, b) => b.timestamp.localeCompare(a.timestamp));

const latestFile = txtFiles[0];

console.log(`‚ÑπÔ∏è  Found ${txtFiles.length} manuscript(s). Using latest: ${latestFile.name}`);

return {
  json: {
    latest_manuscript: latestFile.name,
    manuscript_path: latestFile.path,
    manuscript_url: latestFile.download_url,
    total_manuscripts: txtFiles.length,
    all_manuscripts: txtFiles.map(f => f.name)
  }
};

// Structured Logger Utility
// Provides structured logging to n8n execution logs
// Note: n8n Code nodes don't support fs module, so logs go to console only

const LOG_LEVELS = { DEBUG: 0, INFO: 1, WARN: 2, ERROR: 3 };
const CURRENT_LOG_LEVEL = LOG_LEVELS.INFO;

// Log function that can be referenced by other nodes
function createLogEntry(level, phase, message, metadata = {}) {
  if (LOG_LEVELS[level] < CURRENT_LOG_LEVEL) return null;

  const logEntry = {
    timestamp: new Date().toISOString(),
    level: level,
    workflow: 'Autonomous Ghostwriter Pipeline',
    execution_id: $execution.id || 'unknown',
    phase: phase,
    message: message,
    metadata: metadata
  };

  // Log to n8n console in structured format
  const emoji = { DEBUG: 'üîç', INFO: '‚ÑπÔ∏è', WARN: '‚ö†Ô∏è', ERROR: '‚ùå' }[level] || '';
  console.log(`${emoji} [${level}] ${phase}: ${message}`);
  if (Object.keys(metadata).length > 0) {
    console.log('  Metadata:', JSON.stringify(metadata, null, 2));
  }

  return logEntry;
}

// Return logger info
return [{
  json: {
    log_initialized: true,
    log_level: 'INFO',
    available_levels: ['DEBUG', 'INFO', 'WARN', 'ERROR'],
    note: 'Logs output to n8n execution console'
  }
}];

// Validation: Check Master Planner Response
const response = $input.item.json;

const validation = {
  has_choices: Array.isArray(response.choices) && response.choices.length > 0,
  has_content: !!response.choices?.[0]?.message?.content,
  content_length: response.choices?.[0]?.message?.content?.length || 0,
  word_count: (response.choices?.[0]?.message?.content || '').split(/\s+/).length,
  has_usage: !!response.usage,
  tokens_used: response.usage?.total_tokens || 0
};

const MIN_WORDS = 100;
const MIN_TOKENS = 500;

const isValid = 
  validation.has_choices &&
  validation.has_content &&
  validation.word_count >= MIN_WORDS &&
  validation.tokens_used >= MIN_TOKENS;

if (!isValid) {
  console.error('‚ùå Master Planner validation failed:', validation);
  return [{
    json: {
      validation_failed: true,
      validation_report: validation,
      required_words: MIN_WORDS,
      required_tokens: MIN_TOKENS,
      node: 'PHASE 1: Master Planner'
    }
  }];
}

console.log(`‚úÖ Master Planner validation passed: ${validation.word_count} words, ${validation.tokens_used} tokens`);
return [$input.item];

// Parse Plan is now already compiled
const plan = $input.item.json;

console.log('‚ÑπÔ∏è  Using compiled improvement plan');
console.log('   Chapters analyzed:', plan.improvement_plan.chapter_analyses.length);

return {
    json: {
        improvement_plan: plan.improvement_plan,
        manuscript: plan.manuscript,
        book_id: plan.book_id
    }
};

// Parse manuscript into existing 15 chapters for analysis
const plan = $input.item.json.improvement_plan;
const manuscript = $('Decode Manuscript').first().json.data;
const chapters = manuscript.split(/CHAPTER (?:undefined|\d+)/gi).filter(c => c.trim().length > 100);

console.log(`‚ÑπÔ∏è  Found ${chapters.length} existing chapters in manuscript`);

if (chapters.length !== 15) {
  console.warn(`‚ö†Ô∏è  Expected 15 chapters, found ${chapters.length}`);
}

const expansionItems = [];

// Add chapter expansions
if (plan.expansion_plan?.chapters_to_expand) {
  plan.expansion_plan.chapters_to_expand.forEach(exp => {
    const chapterNum = exp.chapter;
    const chapterText = chapters[chapterNum - 1] || '';
    const wordCount = chapterText.split(/\s+/).length;

    console.log(`   Chapter ${chapterNum}: ${wordCount} words ‚Üí target ${exp.target_words} words (+${exp.estimated_words})`);

    expansionItems.push({
      json: {
        type: 'expand_chapter',
        chapter_number: chapterNum,
        current_text: chapterText,
        current_word_count: wordCount,
        expansion_method: exp.expansion_method,
        scene_outline: exp.scene_outline,
        placement: exp.placement,
        target_words: exp.estimated_words,
        manuscript: manuscript
      }
    });
  });
}

// Add new chapters (villain POV chapter 8.5, etc.)
if (plan.expansion_plan?.new_chapters) {
  plan.expansion_plan.new_chapters.forEach(newCh => {
    console.log(`   NEW Chapter ${newCh.chapter}: ${newCh.target_words} words (${newCh.character} POV)`);

    expansionItems.push({
      json: {
        type: 'new_chapter',
        chapter_number: newCh.chapter,
        character: newCh.character,
        purpose: newCh.purpose,
        target_words: newCh.target_words,
        key_reveals: newCh.key_reveals,
        manuscript: manuscript
      }
    });
  });
}

console.log(`‚ÑπÔ∏è  Created ${expansionItems.length} expansion tasks`);
console.log(`   Target: ${plan.diagnostic_summary.current_word_count} ‚Üí ${plan.diagnostic_summary.target_word_count} words`);

return expansionItems;

