// Validation: Check Scene Writer Response
const response = $input.item.json;

const validation = {
  has_choices: Array.isArray(response.choices) && response.choices.length > 0,
  has_content: !!response.choices?.[0]?.message?.content,
  content_length: response.choices?.[0]?.message?.content?.length || 0,
  word_count: (response.choices?.[0]?.message?.content || '').split(/\s+/).length,
  tokens_used: response.usage?.total_tokens || 0
};

const MIN_WORDS = 200;
const MIN_TOKENS = 800;

const isValid = 
  validation.has_choices &&
  validation.has_content &&
  validation.word_count >= MIN_WORDS &&
  validation.tokens_used >= MIN_TOKENS;

if (!isValid) {
  console.error('‚ùå Scene Writer validation failed:', validation);
  return [{
    json: {
      validation_failed: true,
      validation_report: validation,
      required_words: MIN_WORDS,
      required_tokens: MIN_TOKENS,
      node: 'Scene Writer AI',
      chapter: $('PHASE 2: Expansion Loop').item.json.chapter_number
    }
  }];
}

console.log(`‚úÖ Scene Writer validation passed: ${validation.word_count} words`);
return [$input.item];

const newScene = $input.item.json.choices[0].message.content;
const taskData = $('PHASE 2: Expansion Loop').item.json;
const wordCount = newScene.split(/\\s+/).length;

console.log(`‚ÑπÔ∏è  Chapter ${taskData.chapter_number} ${taskData.type} completed: ${wordCount} words`);

return {
  json: {
    chapter_number: taskData.chapter_number,
    type: taskData.type,
    new_content: newScene,
    word_count: wordCount,
    placement: taskData.placement
  }
};

// Compile all expansions and insert into manuscript
const allExpansions = $input.all();
let manuscript = $('Parse Plan').first().json.manuscript;

console.log(`‚ÑπÔ∏è  Compiling ${allExpansions.length} new scenes into manuscript`);

// Sort by chapter number
const sorted = allExpansions.sort((a, b) => {
  const aNum = parseFloat(a.json.chapter_number);
  const bNum = parseFloat(b.json.chapter_number);
  return aNum - bNum;
});

// Insert new content
const chapters = manuscript.split(/CHAPTER undefined/gi);
let newManuscript = '';

sorted.forEach((exp, idx) => {
  const chNum = Math.floor(parseFloat(exp.json.chapter_number));

  if (exp.json.type === 'new_chapter') {
    console.log(`   + Inserting new Chapter ${exp.json.chapter_number}`);
    if (chNum <= chapters.length) {
      chapters.splice(chNum, 0, '\n\n' + exp.json.new_content + '\n\n');
    }
  } else if (exp.json.type === 'expand_chapter') {
    console.log(`   + Expanding Chapter ${exp.json.chapter_number}`);
    if (chNum > 0 && chNum <= chapters.length) {
      chapters[chNum - 1] = chapters[chNum - 1] + '\n\n' + exp.json.new_content;
    }
  }
});

// Reassemble with proper chapter headers
chapters.forEach((ch, idx) => {
  if (ch.trim().length > 0) {
    newManuscript += `\n\nCHAPTER ${idx + 1}\n\n` + ch.trim();
  }
});

const newWordCount = newManuscript.split(/\\s+/).length;
console.log(`‚ÑπÔ∏è  Expanded manuscript: ${newWordCount} words`);

return {
  json: {
    manuscript_expanded: newManuscript,
    word_count: newWordCount,
    expansions_applied: allExpansions.length,
    book_id: $('Parse Plan').first().json.book_id
  }
};

// Split manuscript into chapters for line editing
const manuscript = $input.item.json.manuscript_expanded;
const chapters = manuscript.split(/CHAPTER \\d+/gi).filter(c => c.trim().length > 100);

const chapterItems = chapters.map((ch, idx) => ({
  json: {
    chapter_number: idx + 1,
    chapter_text: ch.trim(),
    kill_list: [
      'tapestry of',
      'eyes locked',
      'eyes met',
      'the weight of',
      'seemed to',
      'felt like',
      'a mixture of',
      'a blend of'
    ]
  }
}));

console.log(`‚ÑπÔ∏è  Preparing ${chapterItems.length} chapters for line editing`);

return chapterItems;

