const qaResponse = $input.item.json.choices[0].message.content;
const manuscript = $('Compile Line-Edited Manuscript').first().json.manuscript_line_edited;

let qaReport;
try {
  const jsonMatch = qaResponse.match(/```json\n([\\s\\S]*?)\n```/) || qaResponse.match(/```\n([\\s\\S]*?)\n```/);
  qaReport = jsonMatch ? JSON.parse(jsonMatch[1]) : JSON.parse(qaResponse);
} catch (e) {
  console.error('‚ùå Failed to parse QA report:', e);
  qaReport = { raw: qaResponse, parsed: false, error: e.message };
}

const p1Errors = qaReport.errors?.filter(e => e.severity === 'P1').length || 0;
const p2Errors = qaReport.errors?.filter(e => e.severity === 'P2').length || 0;

console.log(`‚ÑπÔ∏è  QA Report:`);
console.log(`   P1 Errors (Critical): ${p1Errors}`);
console.log(`   P2 Errors (Moderate): ${p2Errors}`);

return {
  json: {
    qa_report: qaReport,
    manuscript: manuscript,
    p1_errors: p1Errors,
    p2_errors: p2Errors,
    book_id: $('Compile Line-Edited Manuscript').first().json.book_id
  }
};

