parameters.jsCode:
const criticResponse = $input.item.json.choices[0].message.content;
const manuscript = $('Parse QA Report').first().json.manuscript;
const iteration = $('Configuration').first().json.iteration || 0;

let criticReport;
try {
  const jsonMatch = criticResponse.match(/```json\n([\\s\\S]*?)\n```/) || criticResponse.match(/```\n([\\s\\S]*?)\n```/);
  criticReport = jsonMatch ? JSON.parse(jsonMatch[1]) : JSON.parse(criticResponse);
} catch (e) {
  console.error('‚ùå Failed to parse critic report:', e);
  criticReport = { raw: criticResponse, parsed: false, error: e.message };
}

const scores = criticReport.scores || {};
const lowestScore = Math.min(...Object.values(scores).filter(v => typeof v === 'number'));
const avgScore = Object.values(scores).filter(v => typeof v === 'number').reduce((a, b) => a + b, 0) / Object.values(scores).filter(v => typeof v === 'number').length;

const revisionNeeded = criticReport.revision_needed || lowestScore < 7 || avgScore < 7.5;

// Prevent infinite iteration loop
const MAX_ITERATIONS = 2;
if (iteration >= MAX_ITERATIONS && revisionNeeded) {
  console.warn(`‚ö†Ô∏è  Max iterations (${MAX_ITERATIONS}) reached. Forcing manual review.`);
  return {
    json: {
      critic_report: criticReport,
      manuscript_final: manuscript,
      revision_needed: true,
      max_iterations_reached: true,
      average_score: avgScore,
      lowest_score: lowestScore,
      book_id: $('Parse QA Report').first().json.book_id,
      word_count: manuscript.split(/\\s+/).length
    }
  };
}

console.log(`‚ÑπÔ∏è  Self-Critic Results:`);
console.log(`   Average Score: ${avgScore.toFixed(1)}/10`);
console.log(`   Lowest Score: ${lowestScore}/10`);
console.log(`   Revision Needed: ${revisionNeeded ? 'YES ‚ö†Ô∏è' : 'NO ‚úÖ'}`);

if (!revisionNeeded) {
  console.log(`‚úÖ MANUSCRIPT APPROVED FOR PUBLICATION!`);
}

return {
  json: {
    critic_report: criticReport,
    manuscript_final: manuscript,
    revision_needed: revisionNeeded,
    average_score: avgScore,
    lowest_score: lowestScore,
    book_id: $('Parse QA Report').first().json.book_id,
    word_count: manuscript.split(/\\s+/).length
  }
};

console.log('‚ö†Ô∏è  REVISION NEEDED - Scores too low');
console.log('   Manuscript requires human review or additional iteration.');

if ($json.max_iterations_reached) {
  console.log('\n‚ùå Max iterations reached. Manual intervention required.');
}

console.log('\nOptions:');
console.log('1. Review critic report and manually fix issues');
console.log('2. Run workflow again with adjusted prompts');
console.log('3. Send to human ghostwriter');

return {
  json: {
    status: 'FAILED_QA',
    message: $json.max_iterations_reached ? 'Max iterations reached' : 'Manuscript did not pass self-critic review',
    critic_report: $json.critic_report,
    average_score: $json.average_score,
    book_id: $json.book_id
  }
};

// SYSTEM PROMPT: Master Planner
const system_prompt = `You are a Master Planner for fiction ghostwriting automation. Analyze the manuscript and create a detailed improvement plan that other AI agents will execute WITHOUT human approval.

‚ö†Ô∏è CRITICAL: This manuscript ALREADY HAS 15 CHAPTERS (approximately 22,601 words).

Your job is to EXPAND existing chapters and ADD 1 new chapter, NOT create chapters from scratch.

ANALYSIS FRAMEWORK:
1. Identify which of the 15 existing chapters are too thin (< 1,800 words)
2. Identify structural weaknesses (rushed pacing, unclear stakes)
3. Identify prose issues (purple prose, telling vs showing, repetitive phrases)
4. Identify continuity issues (timeline, magic rules, character consistency)
5. Calculate exact word additions needed: Current 22.6K + Expansions + 1 new chapter = Target 47K

OUTPUT REQUIREMENTS:
- Must be valid JSON
- Be SPECIFIC about what to write (not just "improve X")
- Provide scene outlines with 5-7 bullet points
- Specify exact placement (after which paragraph/scene)
- Include character emotional states and relationship status

JSON SCHEMA:
{
  "diagnostic_summary": {
    "structural_issues": ["issue 1", "issue 2"],
    "prose_issues": ["issue 1", "issue 2"],
    "consistency_issues": ["issue 1", "issue 2"],
    "current_word_count": 22600,
    "target_word_count": 47000
  },
  "expansion_plan": {
    "chapters_to_expand": [
      {
        "chapter": 6,
        "current_words": 1500,
        "target_words": 2800,
        "expansion_method": "insert_new_scene",
        "scene_outline": "Detailed 5-7 bullet outline of what happens",
        "placement": "after_seer_meeting",
        "estimated_words": 1300
      }
    ],
    "new_chapters": [
      {
        "chapter": "8.5",
        "type": "villain_pov",
        "character": "Seraphina",
        "purpose": "Add moral complexity and backstory",
        "target_words": 1400,
        "key_reveals": ["reveal 1", "reveal 2", "reveal 3"]
      }
    ]
  },
  "rewrite_targets": [
    {
      "location": "Chapter 10, paragraph 3",
      "issue": "Gideon disappears with no explanation",
      "solution": "Add scene in Ch 9 where Gideon discovers ward weakness"
    }
  ],
  "prose_priorities": {
    "kill_list_targets": ["tapestry of", "eyes locked"],
    "chapters_needing_heavy_line_edit": [1, 3, 6],
    "telling_to_showing_conversions": 35
  }
}

CRITICAL: Focus on the MIDDLE of the manuscript (Ch 6-10). That's where most expansion is needed.`;

return { json: { system_prompt } };

// Split manuscript into individual chapters for analysis
const manuscript = $('Decode Manuscript').first().json.data;
const config = $('Configuration').first().json;

// Find all chapter markers with their positions
const chapterRegex = /CHAPTER (\d+)/gi;
const matches = [];
let match;

while ((match = chapterRegex.exec(manuscript)) !== null) {
    matches.push({
        chapterNumber: parseInt(match[1]),
        position: match.index,
        fullMatch: match[0]
    });
}

console.log(`‚ÑπÔ∏è  Found ${matches.length} chapter markers`);

if (matches.length === 0) {
    throw new Error('No CHAPTER markers found in manuscript. Make sure chapters are labeled "CHAPTER 1", "CHAPTER 2", etc.');
}

// Extract chapter text between markers
const chapterItems = [];

for (let i = 0; i < matches.length; i++) {
    const currentMatch = matches[i];
    const nextMatch = matches[i + 1];

    // Extract text from current chapter marker to next chapter marker (or end of manuscript)
    const startPos = currentMatch.position + currentMatch.fullMatch.length;
    const endPos = nextMatch ? nextMatch.position : manuscript.length;

    const chapterText = manuscript.substring(startPos, endPos).trim();
    const wordCount = chapterText.split(/\s+/).filter(w => w.length > 0).length;

    console.log(`   Chapter ${currentMatch.chapterNumber}: ${wordCount} words`);

    // Only include if it has substantial content (> 100 words)
    if (wordCount > 100) {
        chapterItems.push({
            json: {
                chapter_number: currentMatch.chapterNumber,
                chapter_text: chapterText,
                word_count: wordCount,
                known_issues: config.known_issues,
                target_word_count: config.target_word_count,
                total_chapters: matches.length,
                // No accumulation needed here
            }
        });
    } else {
        console.log(`   ‚ö†Ô∏è  Chapter ${currentMatch.chapterNumber} too short, skipping`);
    }
}

console.log(`\n‚ÑπÔ∏è  Split into ${chapterItems.length} valid chapters`);

return chapterItems;

// Build request to analyze ONE chapter
const chapterData = $input.first().json;
const systemPrompt = $('PROMPT: Master Planner').first().json.system_prompt;

// Build focused user prompt for this chapter
const userPrompt = `Analyze CHAPTER ${chapterData.chapter_number} of ${chapterData.total_chapters}:

CHAPTER TEXT (${chapterData.word_count} words):
${chapterData.chapter_text}

KNOWN ISSUES FOR THIS MANUSCRIPT:
${chapterData.known_issues.join('\n')}

TARGET: Expand from current ${chapterData.word_count} words to ~${Math.round(chapterData.target_word_count / chapterData.total_chapters)} words per chapter.

Analyze this chapter and provide:
1. Current issues (pacing, prose, character development)
2. Expansion opportunities (scenes to add, depth needed)
3. Specific improvements needed

Output in JSON format with keys: chapter, current_issues, expansion_plan, estimated_words_to_add`;

const requestBody = {
    model: 'gpt-4o',
    messages: [
        {
            role: 'system',
            content: systemPrompt
        },
        {
            role: 'user',
            content: userPrompt
        }
    ],
    temperature: 0.7,
    max_tokens: 2000
};

console.log(`‚ÑπÔ∏è  Built analysis request for Chapter ${chapterData.chapter_number}`);
console.log(`   Chapter: ${chapterData.word_count} words`);

return {
    json: {
        request_body: requestBody,
        chapter_number: chapterData.chapter_number,
        chapter_text: chapterData.chapter_text,
        word_count: chapterData.word_count
    }
};

// Store the analysis for this chapter
const response = $input.first().json;
const loopItem = $('PHASE 1: Chapter Analysis Loop').item.json;
const chapterNumber = loopItem.chapter_number;

// Extract analysis
const analysisText = response.choices[0].message.content;

let chapterAnalysis;
try {
    const jsonMatch = analysisText.match(/```json\n([\\s\\S]*?)\n```/) || analysisText.match(/```\n([\\s\\S]*?)\n```/);
    if (jsonMatch) {
        chapterAnalysis = JSON.parse(jsonMatch[1]);
    } else {
        chapterAnalysis = JSON.parse(analysisText);
    }
} catch (e) {
    chapterAnalysis = {
        chapter: chapterNumber,
        raw_analysis: analysisText
    };
}

console.log(`‚ÑπÔ∏è  Analyzed Chapter ${chapterNumber}`);

// Just output this chapter's analysis - Compile will use $input.all()
return {
    json: {
        chapter_number: chapterNumber,
        analysis: chapterAnalysis,
        word_count: loopItem.word_count || 0,
        tokens_used: response.usage?.total_tokens || 0
    }
};

// Collect ALL chapter analyses using $input.all()
const allAnalyses = $input.all();
const config = $('Configuration').first().json;
const manuscript = $('Decode Manuscript').first().json.data;

console.log(`‚ÑπÔ∏è  Compiling ${allAnalyses.length} chapter analyses`);

if (allAnalyses.length === 0) {
    console.error('‚ùå No analyses received!');
    throw new Error('No chapter analyses received. Loop may not have completed.');
}

// Sort by chapter number
const sorted = allAnalyses
    .map(item => item.json)
    .sort((a, b) => a.chapter_number - b.chapter_number);

console.log(`   Chapters: ${sorted.map(a => a.chapter_number).join(', ')}`);

// Build improvement plan
const improvementPlan = {
    diagnostic_summary: {
        total_chapters_analyzed: sorted.length,
        current_word_count: manuscript.split(/\s+/).length,
        target_word_count: config.target_word_count,
        structural_issues: [],
        prose_issues: config.known_issues
    },
    expansion_plan: {
        chapters_to_expand: [],
        new_chapters: []
    },
    chapter_analyses: []
};

// Process each analysis
sorted.forEach(item => {
    const chapterNum = item.chapter_number;
    const analysis = item.analysis;

    improvementPlan.chapter_analyses.push({
        chapter: chapterNum,
        analysis: analysis || {},
        tokens_used: item.tokens_used || 0
    });

    if (analysis && typeof analysis === 'object') {
        if (analysis.expansion_plan) {
            improvementPlan.expansion_plan.chapters_to_expand.push({
                chapter: chapterNum,
                current_words: item.word_count || 0,
                expansion_method: 'scene_expansion',
                scene_outline: typeof analysis.expansion_plan === 'string'
                    ? analysis.expansion_plan
                    : JSON.stringify(analysis.expansion_plan),
                estimated_words: analysis.estimated_words_to_add || 1000,
                placement: 'throughout_chapter'
            });
        }

        if (analysis.current_issues) {
            const issues = Array.isArray(analysis.current_issues)
                ? analysis.current_issues
                : [`Chapter ${chapterNum}: ${analysis.current_issues}`];
            improvementPlan.diagnostic_summary.structural_issues.push(...issues);
        }
    }
});

const totalTokens = sorted.reduce((sum, item) => sum + (item.tokens_used || 0), 0);

console.log(`\n‚úÖ Compiled ${sorted.length} chapters`);
console.log(`   Expansions: ${improvementPlan.expansion_plan.chapters_to_expand.length}`);
console.log(`   Tokens: ${totalTokens}`);

return {
    json: {
        improvement_plan: improvementPlan,
        manuscript: manuscript,
        book_id: config.book_id,
        total_tokens: totalTokens,
        chapters_analyzed: sorted.length
    }
};

// Verify that plan is complete and ready
const plan = $input.item.json.improvement_plan;

const expectedChapters = 15;
const analyzedChapters = plan.chapter_analyses?.length || 0;

if (analyzedChapters < expectedChapters) {
    throw new Error(`Incomplete plan: only ${analyzedChapters} of ${expectedChapters} chapters analyzed`);
}

console.log(`‚úÖ Plan verification passed:`);
console.log(`   Analyzed: ${analyzedChapters} chapters`);
console.log(`   Expansions planned: ${plan.expansion_plan.chapters_to_expand.length}`);

return [$input.item];
