parameters.jsCode:
const plannerResponse = $input.item.json.choices[0].message.content;
const manuscript = $('Load Manuscript').first().json.data;

// Parse JSON from response
let improvementPlan;
try {
  // Try to extract JSON from markdown code blocks if present
  const jsonMatch = plannerResponse.match(/```json\n([\\s\\S]*?)\n```/) || plannerResponse.match(/```\n([\\s\\S]*?)\n```/);
  if (jsonMatch) {
    improvementPlan = JSON.parse(jsonMatch[1]);
  } else {
    improvementPlan = JSON.parse(plannerResponse);
  }
} catch (e) {
  console.error('Failed to parse improvement plan:', e);
  improvementPlan = { error: 'Parse failed', raw: plannerResponse };
}

console.log('ðŸ“‹ Master Plan Generated');
console.log('Chapters to expand:', improvementPlan.expansion_plan?.chapters_to_expand?.length || 0);
console.log('New chapters to write:', improvementPlan.expansion_plan?.new_chapters?.length || 0);
console.log('Sections to rewrite:', improvementPlan.rewrite_targets?.length || 0);

return {
  json: {
    improvement_plan: improvementPlan,
    manuscript: manuscript,
    book_id: $('Configuration').first().json.book_id
  }
};

// Parse manuscript into existing 15 chapters for analysis
const plan = $input.item.json.improvement_plan;
const manuscript = $input.item.json.manuscript;
const chapters = manuscript.split(/CHAPTER (?:undefined|\d+)/gi).filter(c => c.trim().length > 100);

console.log(`ðŸ“Š Found ${chapters.length} existing chapters in manuscript`);

if (chapters.length !== 15) {
  console.warn(`âš ï¸  Expected 15 chapters, found ${chapters.length}`);
}

const expansionItems = [];

// Add chapter expansions (Master Planner identifies which of 15 existing chapters need expansion)
if (plan.expansion_plan?.chapters_to_expand) {
  plan.expansion_plan.chapters_to_expand.forEach(exp => {
    const chapterNum = exp.chapter;
    const chapterText = chapters[chapterNum - 1] || '';
    const wordCount = chapterText.split(/\s+/).length;

    console.log(`  Chapter ${chapterNum}: ${wordCount} words â†’ target ${exp.target_words} words (+${exp.estimated_words})`);

    expansionItems.push({
      json: {
        type: 'expand_chapter',
        chapter_number: chapterNum,
        current_text: chapterText,
        current_word_count: wordCount,
        expansion_method: exp.expansion_method,
        scene_outline: exp.scene_outline,
        placement: exp.placement,
        target_words: exp.estimated_words,
        manuscript: manuscript
      }
    });
  });
}

// Add new chapters (villain POV chapter 8.5, etc.)
if (plan.expansion_plan?.new_chapters) {
  plan.expansion_plan.new_chapters.forEach(newCh => {
    console.log(`  NEW Chapter ${newCh.chapter}: ${newCh.target_words} words (${newCh.character} POV)`);

    expansionItems.push({
      json: {
        type: 'new_chapter',
        chapter_number: newCh.chapter,
        character: newCh.character,
        purpose: newCh.purpose,
        target_words: newCh.target_words,
        key_reveals: newCh.key_reveals,
        manuscript: manuscript
      }
    });
  });
}

console.log(`\nðŸ”„ Created ${expansionItems.length} expansion tasks`);
console.log(`ðŸ“ˆ Target: ${plan.diagnostic_summary.current_word_count} â†’ ${plan.diagnostic_summary.target_word_count} words`);

return expansionItems;

